INSERT INTO `contents` VALUES (127, 'Android Fase 2', NULL, 'Mempelajari pembuatan aplikasi Android dengan Kotlin yang mencakup desain modular, keamanan, pengujian UI, penggunaan Jetpack Compose, manajemen tugas latar belakang, dan penerapan di Play Store.', '<p style=\"margin-bottom: 0px; text-shadow: rgba(0, 0, 0, 0.1) 0px 14px 30px; opacity: 1;\" class=\"\"><h5 class=\"\">Kurikulum yang dipelajari</h5><ul><li>Pengembangan Modular &amp; Keamanan<br>Fokus pada pembuatan aplikasi Android dengan pendekatan modular dan penerapan praktik keamanan yang ketat.</li><li>Pengujian &amp; Interface User<br>Mengedepankan pengujian instrumen dan UI untuk memastikan fungsionalitas dan interaksi pengguna yang lancar.</li><li>Desain Modern dengan Jetpack Compose<br>Memperkenalkan Jetpack Compose untuk mengembangkan UI yang lebih efisien dan modern.</li><li>Manajemen Proyek &amp; Publikasi<br>Mengimplementasikan manajemen proyek dengan Jira dan mempelajari proses publikasi aplikasi ke Google Play Store.</li><li>Platform atau Tools yang akan digunakan</li></ul><p></p><p></p></p>\n', NULL, NULL, NULL, NULL, '15', 1, '2024-02-15 11:13:13', '2024-02-20 09:21:49', '2024-02-20 09:21:49', 1, 1, 1, 120, NULL);
INSERT INTO `contents` VALUES (128, 'Fullstack Javascript', 'Bootcamp', 'Belajar Menjadi Fullstack Developer dengan Javascript dalam hanya 12 minggu. kuasai front-end dan back-end siapkan diri menjadi pengembang web fullstack dan buka peluang karir di industri IT', '{\"sub_konten\":[{\"konten_index\":\"1708073462316\",\"konten_title\":\"Kenapa Harus Belajar Fullstack Javascript?\",\"konten_description\":[{\"sub_konten_index\":\"1708073478\",\"sub_konten_title\":\"Web Mastering\",\"sub_konten_image\":null,\"sub_konten_description\":\"Belajar Fullstack Javascript memungkinkan kamu merancang dan membangun solusi web end-to-end, menempatkanmu di garis depan transformasi digital.\"},{\"sub_konten_index\":\"1708073494\",\"sub_konten_title\":\"Skill yang Beradaptasi dengan Inovasi\",\"sub_konten_image\":null,\"sub_konten_description\":\"Kuasai Javascript dan kamu akan dapat menciptakan aplikasi yang berjalan mulus di browser, server, dan bahkan perangkat mobile.\"},{\"sub_konten_index\":\"1708073506\",\"sub_konten_title\":\"Ekspansi Karier Luas\",\"sub_konten_image\":null,\"sub_konten_description\":\"Fullstack Javascript membuka kesempatan karier yang luas, menyiapkanmu untuk berbagai posisi teknis dari pengembang hingga analis sistem.\"}]},{\"konten_index\":\"1708073517066\",\"konten_title\":\"Destinasi Karier Profesional Setelah Lulus\",\"konten_description\":[{\"sub_konten_index\":\"1708073529\",\"sub_konten_title\":null,\"sub_konten_image\":\"1708073529-1-subkonten.png\",\"sub_konten_description\":null},{\"sub_konten_index\":\"1708073542\",\"sub_konten_title\":null,\"sub_konten_image\":\"1708073542-1-subkonten.png\",\"sub_konten_description\":null},{\"sub_konten_index\":\"1708073550\",\"sub_konten_title\":null,\"sub_konten_image\":\"1708073550-1-subkonten.png\",\"sub_konten_description\":null},{\"sub_konten_index\":\"1708073557\",\"sub_konten_title\":null,\"sub_konten_image\":\"1708073557-1-subkonten.png\",\"sub_konten_description\":null}]},{\"konten_index\":\"17080735