INSERT INTO `contents` VALUES (160, 'Dasar Pemrograman Web', NULL, 'Memahami HTML dan CSS untuk pembuatan tampilan web yang responsif serta Javascript untuk logika dasar, meletakkan pondasi kuat dalam pengembangan web modern.', NULL, NULL, NULL, NULL, NULL, '15', 1, '2024-02-20 15:43:14', '2024-02-20 15:43:14', NULL, 1, NULL, NULL, 124, NULL);
INSERT INTO `contents` VALUES (161, 'Javascript Esensial', NULL, 'Menyelami lebih dalam ke Javascript, mempelajari konsep seperti asynchronous programming, closure, dan penggunaan library seperti React untuk antarmuka yang interaktif.', NULL, NULL, NULL, NULL, NULL, '15', 1, '2024-02-20 15:43:41', '2024-02-20 15:43:41', NULL, 1, NULL, NULL, 124, NULL);
INSERT INTO `contents` VALUES (162, 'Pengenalan Tools Pengembangan', NULL, 'Belajar menggunakan Android Studio untuk pengembangan aplikasi dan integrasi dengan API untuk data dinamis dan interaksi pengguna.', NULL, NULL, NULL, NULL, NULL, '15', 1, '2024-02-20 15:44:15', '2024-02-20 15:44:15', NULL, 1, NULL, NULL, 124, NULL);
INSERT INTO `contents` VALUES (163, 'Proyek Fullstack & Deploy', NULL, 'Mengembangkan aplikasi fullstack dengan Node.js dan Express untuk backend, serta mengintegrasikan database dan API, diakhiri dengan pengalaman deploy aplikasi ke server.', NULL, NULL, NULL, NULL, NULL, '15', 1, '2024-02-20 15:44:45', '2024-02-20 15:44:45', NULL, 1, NULL, NULL, 124, NULL);
